/**
 * 在字符串中找出出现最多的字符
 * @param str
 * @returns {{maxValue: null, maxNum: number}}
 */
function findMaxString (str) {
  const strArr = [...str]
  const storeObj = {}
  const maxObj = {
    maxNum :0,
    maxValue:null
  }
  strArr.forEach((item) => {
    storeObj[item] = storeObj[item] === undefined ? 1 : storeObj[item]+1
    if (storeObj[item] > maxObj.maxNum) {
      maxObj.maxNum = storeObj[item]
      maxObj.maxValue = item
    }
  })
  return maxObj
}

export {
  findMaxString
}
